#!/bin/bash

# iOS Simulator Log Streamer
# Automatically streams logs from the active iOS Simulator to terminal
# Usage: ./scripts/streamSimulatorLogs.sh

echo "🔍 Finding active iOS Simulator..."

# Get the booted simulator UDID
SIM_UDID=$(xcrun simctl list devices | grep "Booted" | head -1 | grep -oE '[A-F0-9-]{36}')

if [ -z "$SIM_UDID" ]; then
  echo "❌ No booted simulator found. Please start a simulator first."
  exit 1
fi

SIM_NAME=$(xcrun simctl list devices | grep "$SIM_UDID" | sed 's/.*(\(.*\))/\1/' | sed 's/).*//')
echo "✅ Found simulator: $SIM_NAME ($SIM_UDID)"
